import React, { useState } from 'react';
import axios from 'axios';

function Signup() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/auth/register', { username, password });
      alert('Registration successful. You can now login.');
    } catch (err) {
      alert('Signup failed');
    }
  };

  return (
    <form onSubmit={handleSignup} className="p-4 bg-white rounded shadow max-w-sm mx-auto mt-8">
      <h2 className="text-lg font-bold mb-2">Signup</h2>
      <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required className="w-full mb-2 border" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required className="w-full mb-2 border" />
      <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">Signup</button>
    </form>
  );
}

export default Signup;
