import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPanel({ token, isAdmin }) {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');
  const [message, setMessage] = useState('');

  const fetchServices = async () => {
    const res = await axios.get('http://localhost:5000/services');
    setServices(res.data);
  };

  const addService = async () => {
    if (!newService.trim()) return;
    try {
      await axios.post('http://localhost:5000/services', { name: newService }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('✅ Service added successfully.');
      setNewService('');
      fetchServices();
    } catch (err) {
      setMessage('❌ Failed to add service.');
    }
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/services/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('✅ Service deleted.');
      fetchServices();
    } catch (err) {
      setMessage('❌ Failed to delete service.');
    }
  };

  useEffect(() => {
    fetchServices();
  }, []);

  if (!isAdmin) return null;

  return (
    <div className="p-4 mt-6 bg-gray-100 rounded shadow">
      <h2 className="font-bold mb-2 text-lg">Admin: Manage Services</h2>
      {message && <div className="mb-2 text-sm text-green-700">{message}</div>}
      <div className="flex gap-2 mb-4">
        <input
          value={newService}
          onChange={e => setNewService(e.target.value)}
          placeholder="New service name"
          className="border w-full p-1"
        />
        <button
          onClick={addService}
          className="bg-green-600 text-white px-3 py-1 rounded"
        >
          Add
        </button>
      </div>
      <ul>
        {services.map(service => (
          <li key={service._id} className="flex justify-between items-center mb-1">
            {service.name}
            <button
              onClick={() => deleteService(service._id)}
              className="text-sm text-red-500 underline"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminPanel;
