import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

function AdminPanel({ token }) {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');
  const [message, setMessage] = useState('');
  const [stats, setStats] = useState(null);

  const fetchServices = async () => {
    const res = await axios.get('http://localhost:5000/services');
    setServices(res.data);
  };

  const fetchStats = async () => {
    const res = await axios.get('http://localhost:5000/bookings/admin/stats', {
      headers: { Authorization: `Bearer ${token}` }
    });
    setStats(res.data);
  };

  const addService = async () => {
    if (!newService.trim()) return;
    try {
      await axios.post('http://localhost:5000/services', { name: newService }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('âœ… Service added successfully.');
      setNewService('');
      fetchServices();
    } catch (err) {
      setMessage('âŒ Failed to add service.');
    }
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/services/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('âœ… Service deleted.');
      fetchServices();
    } catch (err) {
      setMessage('âŒ Failed to delete service.');
    }
  };

  useEffect(() => {
    fetchServices();
    fetchStats();
  }, []);

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md border border-gray-100">
      <h2 className="text-2xl font-bold text-indigo-700 mb-4">ðŸ›  Manage Services</h2>
      {message && <div className="mb-4 text-sm text-green-600 font-medium">{message}</div>}
      <div className="flex flex-col sm:flex-row gap-3 mb-4">
        <input
          value={newService}
          onChange={e => setNewService(e.target.value)}
          placeholder="New service name"
          className="flex-1 border px-4 py-2 rounded-lg shadow-sm"
        />
        <button
          onClick={addService}
          className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
        >
          âž• Add
        </button>
      </div>
      <ul className="mb-6">
        {services.map(service => (
          <li key={service._id} className="flex justify-between items-center border-b py-2">
            <span className="text-gray-800">{service.name}</span>
            <button onClick={() => deleteService(service._id)} className="text-sm text-red-500 hover:underline">ðŸ—‘ Delete</button>
          </li>
        ))}
      </ul>

      {stats && (
        <div className="bg-indigo-50 p-4 rounded-lg">
          <h3 className="text-lg font-semibold text-indigo-700 mb-2">ðŸ“Š System Stats</h3>
          <p>Total Bookings: <strong>{stats.totalBookings}</strong></p>
          <p>Most Popular Service: <strong>{stats.mostPopularService}</strong></p>
          <p>Total Users: <strong>{stats.totalUsers}</strong></p>
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
