import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookingForm({ fetchBookings, token, userId }) {
  const [formData, setFormData] = useState({
    customer_name: '',
    address: '',
    date_time: '',
    service_id: '',
    user_id: userId
  });
  const [services, setServices] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/services').then(res => setServices(res.data));
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    await axios.post('http://localhost:5000/bookings', formData, {
      headers: { Authorization: `Bearer ${token}` }
    });
    fetchBookings();
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white shadow rounded">
      <input name="customer_name" placeholder="Name" required onChange={handleChange} className="mb-2 w-full border" />
      <input name="address" placeholder="Address" required onChange={handleChange} className="mb-2 w-full border" />
      <input name="date_time" type="datetime-local" required onChange={handleChange} className="mb-2 w-full border" />
      <select name="service_id" onChange={handleChange} required className="mb-2 w-full border">
        <option value="">Select a service</option>
        {services.map(service => (
          <option key={service._id} value={service._id}>{service.name}</option>
        ))}
      </select>
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Book</button>
    </form>
  );
}

export default BookingForm;
