import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookingForm({ fetchBookings, token, userId }) {
  const [formData, setFormData] = useState({
    customer_name: '',
    address: '',
    date_time: '',
    service_id: '',
    user_id: userId
  });
  const [services, setServices] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/services').then(res => setServices(res.data));
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/bookings', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('✅ Booking added successfully!');
      setFormData({ customer_name: '', address: '', date_time: '', service_id: '', user_id: userId });
      fetchBookings();
    } catch (err) {
      setMessage('❌ Failed to add booking.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white shadow-md rounded-xl p-6 mb-6">
      <h2 className="text-xl font-semibold text-indigo-700 mb-4">Book a Cleaning Service</h2>
      {message && <p className="mb-3 text-sm text-green-600">{message}</p>}
      <div className="mb-3">
        <label className="block text-sm font-medium text-gray-700 mb-1">Customer Name</label>
        <input name="customer_name" value={formData.customer_name} onChange={handleChange} required className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400" />
      </div>
      <div className="mb-3">
        <label className="block text-sm font-medium text-gray-700 mb-1">Address</label>
        <input name="address" value={formData.address} onChange={handleChange} required className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400" />
      </div>
      <div className="mb-3">
        <label className="block text-sm font-medium text-gray-700 mb-1">Date & Time</label>
        <input type="datetime-local" name="date_time" value={formData.date_time} onChange={handleChange} required className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400" />
      </div>
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-1">Service Type</label>
        <select name="service_id" value={formData.service_id} onChange={handleChange} required className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400">
          <option value="">Select a service</option>
          {services.map(service => (
            <option key={service._id} value={service._id}>{service.name}</option>
          ))}
        </select>
      </div>
      <button type="submit" className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition">Book Now</button>
    </form>
  );
}

export default BookingForm;
