import React, { useState, useEffect } from 'react';
import BookingForm from './components/BookingForm';
import BookingList from './components/BookingList';
import Login from './components/Login';
import Signup from './components/Signup';
import axios from 'axios';

function App() {
  const [bookings, setBookings] = useState([]);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [userId, setUserId] = useState(localStorage.getItem('userId') || '');

  const fetchBookings = async () => {
    const res = await axios.get('http://localhost:5000/bookings', {
      headers: { Authorization: `Bearer ${token}` }
    });
    setBookings(res.data.filter(b => b.user_id?._id === userId));
  };

  useEffect(() => {
    if (token) fetchBookings();
  }, [token]);

  if (!token) return (
    <>
      <Login setToken={setToken} setUserId={setUserId} />
      <Signup />
    </>
  );

  return (
    <div className="p-8 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Cleaning Service Booking</h1>
      <button onClick={() => { localStorage.clear(); setToken(''); }} className="mb-4 text-sm underline text-red-600">Logout</button>
      <BookingForm fetchBookings={fetchBookings} token={token} userId={userId} />
      <BookingList bookings={bookings} />
    </div>
  );
}

export default App;
