import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AuthPage from './components/Login'; // ðŸ‘ˆ this is your combined login/signup component
import BookingForm from './components/BookingForm';
import BookingList from './components/BookingList';
import AdminPanel from './components/AdminPanel';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [userId, setUserId] = useState(localStorage.getItem('userId') || '');
  const [bookings, setBookings] = useState([]);

  const fetchBookings = async () => {
    const res = await axios.get('http://localhost:5000/bookings', {
      headers: { Authorization: `Bearer ${token}` }
    });
    setBookings(res.data.filter(b => b.user_id?._id === userId));
  };

  useEffect(() => {
    if (token) fetchBookings();
  }, [token]);

  const handleLogout = () => {
    localStorage.clear();
    setToken('');
    setUserId('');
  };

  if (!token) {
    return <AuthPage setToken={setToken} setUserId={setUserId} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-indigo-700">Cleaning Service Portal</h1>
          <button
            onClick={handleLogout}
            className="text-sm text-red-600 underline hover:text-red-800 transition"
          >
            Logout
          </button>
        </div>

        <BookingForm fetchBookings={fetchBookings} token={token} userId={userId} />
        <BookingList bookings={bookings} />
        <AdminPanel token={token} isAdmin={true} /> {/* change to dynamic if you implement admin flag */}
      </div>
    </div>
  );
}

export default App;
