import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

function AdminPanel({ token }) {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');
  const [message, setMessage] = useState('');
  const [stats, setStats] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [filters, setFilters] = useState({ service: '', from: '', to: '' });

  const fetchServices = async () => {
    const res = await axios.get('http://localhost:5000/services');
    setServices(res.data);
  };

  const fetchStats = async () => {
    const res = await axios.get('http://localhost:5000/bookings/admin/stats', {
      headers: { Authorization: `Bearer ${token}` }
    });
    setStats(res.data);
  };

  const fetchChartData = async () => {
    const res = await axios.get('http://localhost:5000/bookings', {
      headers: { Authorization: `Bearer ${token}` }
    });
    const grouped = {};
    res.data.forEach(b => {
      const name = b.service_id?.name || 'Unknown';
      if (!grouped[name]) grouped[name] = 0;
      grouped[name] += 1;
    });
    const data = Object.entries(grouped).map(([name, count]) => ({ name, count }));
    setChartData(data);
  };

  const addService = async () => {
    if (!newService.trim()) return;
    try {
      await axios.post('http://localhost:5000/services', { name: newService }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('‚úÖ Service added successfully.');
      setNewService('');
      fetchServices();
    } catch (err) {
      setMessage('‚ùå Failed to add service.');
    }
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/services/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('‚úÖ Service deleted.');
      fetchServices();
    } catch (err) {
      setMessage('‚ùå Failed to delete service.');
    }
  };

  useEffect(() => {
    fetchServices();
    fetchStats();
    fetchChartData();
  }, []);

  return (
    <div className="p-4 mt-6 bg-gray-100 rounded shadow">
      <h2 className="font-bold mb-2 text-lg">Admin: Manage Services</h2>
      {message && <div className="mb-2 text-sm text-green-700">{message}</div>}
      <div className="flex gap-2 mb-4">
        <input value={newService} onChange={e => setNewService(e.target.value)} placeholder="New service name" className="border w-full p-1" />
        <button onClick={addService} className="bg-green-600 text-white px-3 py-1 rounded">Add</button>
      </div>
      <ul>
        {services.map(service => (
          <li key={service._id} className="flex justify-between items-center mb-1">
            {service.name}
            <button onClick={() => deleteService(service._id)} className="text-sm text-red-500 underline">Delete</button>
          </li>
        ))}
      </ul>

      {stats && (
        <div className="mt-6 p-4 bg-indigo-50 rounded-md">
          <h3 className="text-md font-semibold text-indigo-700 mb-2">üìä Admin Stats</h3>
          <p>Total Bookings: <strong>{stats.totalBookings}</strong></p>
          <p>Most Popular Service: <strong>{stats.mostPopularService}</strong></p>
          <p>Total Users: <strong>{stats.totalUsers}</strong></p>
        </div>
      )}

      {chartData.length > 0 && (
        <div className="mt-6">
          <h3 className="text-md font-semibold text-indigo-700 mb-2">üìà Popular Services</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={chartData}>
              <XAxis dataKey="name" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Bar dataKey="count" fill="#6366f1" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}

      <div className="mt-6">
        <h3 className="font-semibold text-indigo-700 mb-2">üîç Filter Bookings</h3>
        <div className="flex gap-4 flex-wrap">
          <select
            value={filters.service}
            onChange={(e) => setFilters({ ...filters, service: e.target.value })}
            className="border px-2 py-1 rounded"
          >
            <option value="">All Services</option>
            {services.map(s => <option key={s._id} value={s.name}>{s.name}</option>)}
          </select>
          <input
            type="date"
            value={filters.from}
            onChange={(e) => setFilters({ ...filters, from: e.target.value })}
            className="border px-2 py-1 rounded"
          />
          <input
            type="date"
            value={filters.to}
            onChange={(e) => setFilters({ ...filters, to: e.target.value })}
            className="border px-2 py-1 rounded"
          />
          <button className="bg-indigo-600 text-white px-3 py-1 rounded">Apply</button>
        </div>
      </div>
    </div>
  );
}

export default AdminPanel;
